import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

interface Post {
  id: number;
  title: string;
  content: string;
  created_at: string;
}

const MyPosts: React.FC = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingPost, setEditingPost] = useState<Post | null>(null);
  const [editForm, setEditForm] = useState({ title: '', content: '' });
  const navigate = useNavigate();
  const { token } = useAuth();

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      if (!token) {
        console.error('No authentication token found');
        setError('Please log in to view your posts');
        setIsLoading(false);
        return;
      }

      console.log('Fetching user posts...');
      console.log('Using token:', token.substring(0, 20) + '...');
      
      const response = await axios.get('http://localhost:5000/api/posts/user', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      console.log('Posts response:', response.data);
      
      // Ensure we have an array of posts
      const postsData = Array.isArray(response.data) ? response.data : response.data.posts || [];
      console.log('Processed posts data:', postsData);
      
      if (!Array.isArray(postsData)) {
        throw new Error('Invalid response format: expected array of posts');
      }
      
      setPosts(postsData);
      setError(''); // Clear any previous errors
    } catch (err: any) {
      console.error('Error fetching posts:', err);
      if (err.response) {
        console.error('Response status:', err.response.status);
        console.error('Response data:', err.response.data);
      }
      const errorMessage = err.response?.data?.message || 'Failed to load posts';
      console.error('Error details:', errorMessage);
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleEdit = (post: Post) => {
    setEditingPost(post);
    setEditForm({ title: post.title, content: post.content });
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingPost) return;

    try {
      console.log('Updating post:', {
        postId: editingPost.id,
        newData: editForm
      });

      const response = await axios.put(
        `http://localhost:5000/api/posts/${editingPost.id}`,
        editForm,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Update response:', response.data);

      if (response.data) {
        setPosts(posts.map(post => 
          post.id === editingPost.id ? response.data : post
        ));
        setEditingPost(null);
        setEditForm({ title: '', content: '' });
      } else {
        throw new Error('No data received from server');
      }
    } catch (err: any) {
      console.error('Error updating post:', err);
      const errorMessage = err.response?.data?.message || 'Failed to update post';
      console.error('Error details:', errorMessage);
      setError(errorMessage);
    }
  };

  const handleDelete = async (postId: number) => {
    if (!window.confirm('Are you sure you want to delete this post?')) {
      return;
    }

    try {
      console.log('Deleting post:', postId);
      
      const response = await axios.delete(
        `http://localhost:5000/api/posts/${postId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      console.log('Delete response:', response.data);

      if (response.data.message === 'Post deleted successfully') {
        setPosts(posts.filter(post => post.id !== postId));
      } else {
        throw new Error('Unexpected response from server');
      }
    } catch (err: any) {
      console.error('Error deleting post:', err);
      const errorMessage = err.response?.data?.message || 'Failed to delete post';
      console.error('Error details:', errorMessage);
      setError(errorMessage);
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="rounded-md bg-red-50 p-4">
        <div className="text-sm text-red-700">{error}</div>
      </div>
    );
  }

  if (!Array.isArray(posts) || posts.length === 0) {
    return (
      <div className="text-center py-12">
        <h3 className="text-lg font-medium text-gray-900">No posts yet</h3>
        <p className="mt-2 text-sm text-gray-500">Get started by creating your first post!</p>
        <div className="mt-6">
          <Link
            to="/create-post"
            className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Create Post
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">My Posts</h2>
        <Link
          to="/create-post"
          className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
        >
          Create New Post
        </Link>
      </div>

      {editingPost && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full">
            <h3 className="text-lg font-medium mb-4">Edit Post</h3>
            <form onSubmit={handleEditSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Title</label>
                <input
                  type="text"
                  value={editForm.title}
                  onChange={(e) => setEditForm({ ...editForm, title: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Content</label>
                <textarea
                  value={editForm.content}
                  onChange={(e) => setEditForm({ ...editForm, content: e.target.value })}
                  rows={6}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                  required
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setEditingPost(null)}
                  className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700"
                >
                  Save Changes
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="grid gap-6">
        {posts.map((post) => (
          <div key={post.id} className="bg-white shadow rounded-lg p-6">
            <h3 className="text-xl font-semibold text-gray-900 mb-2">{post.title}</h3>
            <p className="text-gray-600 mb-4">{post.content}</p>
            <div className="flex justify-between items-center">
              <div className="text-sm text-gray-500">
                Posted on {new Date(post.created_at).toLocaleDateString()}
              </div>
              <div className="space-x-3">
                <button
                  onClick={() => handleEdit(post)}
                  className="text-indigo-600 hover:text-indigo-900"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(post.id)}
                  className="text-red-600 hover:text-red-900"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyPosts;
